//------------------------------------------------------------------------------------------------------
// 图像层深度坐标系代码实现
// 用于实现根据摄像机与图像层显示对象的坐标呈现出的实际显示效果
// -- 编辑该代码后能够在编辑器中立刻呈现出效果出来，编辑器和游戏运行时都会运行该代码使之呈现效果一致
// -- 如果要更改此代码请自行备份，以便能够恢复原始效果
//------------------------------------------------------------------------------------------------------
/**
 * 深度坐标系转为实际坐标系
 * @param sp 显示对象
 */
function dpCoordToRealCoord(sp: GameSprite): void {
    if (!sp.useDPCoord || !sp._dpDirty) return;
    sp._dpDirty = false;
    var cx = Config.WINDOW_WIDTH / 2;
    var cy = Config.WINDOW_HEIGHT / 2;
    // 镜头最大时显示对象放大倍数
    var scaleMax = 20;
    // 正常大小的Z距离
    var normalDistance = 100;
    var normalZ = normalDistance + sp._dpCameraZ;
    var furthestDistance = normalDistance / scaleMax + normalDistance;
    var furthestZ = furthestDistance + sp._dpCameraZ;
    var dpX = sp.dpX + sp._dpCameraX;
    var dpY = sp.dpY + sp._dpCameraY;
    var dpZDistance = sp.dpZ - sp._dpCameraZ;
    // 正常距离
    if (dpZDistance == normalDistance) {
        toX = dpX;
        toY = dpY;
        if (sp.useDPCoordScaleMode) {
            var toScaleX = 1;
            var toScaleY = 1;
        }
        else {
            toW = sp.dpWidth;
            toH = sp.dpHeight;
        }
        sp.dpOpacity = 1;
    }
    // 远距离：缩小，聚集屏幕中心点
    else if (dpZDistance > normalDistance) {
        // 超出最远距离的情况
        if (dpZDistance >= furthestDistance) {
            sp.dpOpacity = 0;
            per = 1;
            toX = cx;
            toY = cy;
            if (sp.useDPCoordScaleMode) {
                toScaleX = 0.001;
                toScaleY = 0.001;
            }
            else {
                toW = 0;
                toH = 0;
            }
        }
        // 普通 => 最远 的情况
        else {
            sp.dpOpacity = 1;
            var per = (dpZDistance - normalDistance) / (furthestDistance - normalDistance);
            if (sp.useDPCoordScaleMode) {
                var toX = (cx - dpX) * per + dpX;
                var toY = (cy - dpY) * per + dpY;
                toScaleX = 1 - per;
                toScaleY = 1 - per;
            }
            else {
                var toX = (cx - dpX) * per + dpX;
                var toY = (cy - dpY) * per + dpY;
                var toW = (0 - sp.dpWidth) * per + sp.dpWidth;
                var toH = (0 - sp.dpHeight) * per + sp.dpHeight;
            }
        }
    }
    // 近距离：放大
    else if (dpZDistance < normalDistance && dpZDistance >= 0) {
        // 0-1 1表示最近
        var near = (normalDistance - dpZDistance) / normalDistance;
        var per = near * scaleMax;
        var sPer = per + 1;
        if (sp.useDPCoordScaleMode) {
            var toX = (dpX - cx) * per + dpX;
            var toY = (dpY - cy) * per + dpY;
            toScaleX = sPer;
            toScaleY = sPer;
        }
        else {
            var toX = (dpX - cx) * per + dpX;
            var toY = (dpY - cy) * per + dpY;
            var toW = sp.dpWidth * sPer;
            var toH = sp.dpHeight * sPer;
        }
        // 透明度效果
        if (near > 0.5) {
            sp.dpOpacity = (1 - (near - 0.5) * 2);
        }
        else {
            sp.dpOpacity = 1;
        }
    }
    // 超过镜头
    else {
        sp.dpOpacity = 0;
    }
    sp.pos(toX, toY);
    // 使用useDPCoordScaleMode的情况：动画、立绘；界面
    if (sp.useDPCoordScaleMode) {
        sp.scaleX = toScaleX * sp.dpScaleX;
        sp.scaleY = toScaleY * sp.dpScaleY;
    }
    // 图片
    else {
        if (sp.width == 0 || sp.height == 0) {
            sp.scaleX = 1;
            sp.scaleY = 1;
        }
        else {
            sp.scaleX = Math.max(toW / sp.width * sp.dpScaleX, 0.001);
            sp.scaleY = Math.max(toH / sp.height * sp.dpScaleY, 0.001);
        }
    }
}
/**
 * 实际坐标系转换为深度坐标系（比如操作工具操作了对象后，需要转换为深度坐标系）
 * @param calcCoord 是否计算坐标
 * @param calcSize 是否计算尺寸
 */
function realCoordToDPCoord(sp: GameSprite, calcCoord: boolean, calcSize: boolean): { dpX: number, dpy: number, dpScaleX: number, dpScaleY: number, dpWidth: number, dpHeight: number } {
    var cx = Config.WINDOW_WIDTH / 2;
    var cy = Config.WINDOW_HEIGHT / 2;
    var scaleMax = 20;
    var normalDistance = 100;
    var normalZ = normalDistance + sp._dpCameraZ;
    var furthestDistance = normalDistance / scaleMax + normalDistance;
    var furthestZ = furthestDistance + sp._dpCameraZ;
    var dpZDistance = sp.dpZ - sp._dpCameraZ;
    // 正常距离
    if (dpZDistance == normalDistance) {
        if (calcCoord) {
            var toDPX = sp.x;
            var toDPY = sp.y;
        }
        if (calcSize) {
            var toDPScaleX = sp.dpWidth == 0 ? 1 : sp.width / sp.dpWidth;
            var toDPScaleY = sp.dpHeight == 0 ? 1 : sp.height / sp.dpHeight;
        }
    }
    // 远距离：缩小，聚集屏幕中心点
    else if (dpZDistance > normalDistance) {
        // 超出最远距离的情况
        if (dpZDistance >= furthestDistance) {
            return;
        }
        // 普通 => 最远 的情况
        else {
            var per = (dpZDistance - normalDistance) / (furthestDistance - normalDistance);
            if (calcCoord) {
                var toDPX = (sp.x - cx * per) / (1 - per);
                var toDPY = (sp.y - cy * per) / (1 - per);
            }
            if (calcSize) {
                var toDPScaleX = sp.width / ((0 - sp.dpWidth) * per + sp.dpWidth)
                var toDPScaleY = sp.height / ((0 - sp.dpHeight) * per + sp.dpHeight);
            }
        }
    }
    // 近距离：放大
    else if (dpZDistance < normalDistance && dpZDistance >= 0) {
        // 0-1 1表示最近
        var near = (normalDistance - dpZDistance) / normalDistance;
        var per = near * scaleMax;
        var sPer = per + 1;
        var toDPX = (sp.x + cx * per) / (1 + per);
        var toDPY = (sp.y + cy * per) / (1 + per);
        var toDPScaleX = sp.width / (sp.dpWidth * sPer);
        var toDPScaleY = sp.height / (sp.dpHeight * sPer);
    }
    // 超过镜头
    else {

    }
    if (calcCoord) {
        sp.dpX = Math.floor(toDPX - sp._dpCameraX);
        sp.dpY = Math.floor(toDPY - sp._dpCameraY);
    }
    if (calcSize) {
        if (sp.useDPCoordScaleMode) {
            sp.dpScaleX = toDPScaleX;
            sp.dpScaleY = toDPScaleY;
        }
        else {
            sp.dpWidth = Math.floor(sp.dpWidth * toDPScaleX);
            sp.dpHeight = Math.floor(sp.dpHeight * toDPScaleY);
            // 如果存在贴图的话（图片对象）
            if (sp._dpTextureWidth != null) {
                sp.scaleX = sp.width / sp._dpTextureWidth;
                sp.scaleY = sp.height / sp._dpTextureHeight;
                sp.width = sp._dpTextureWidth;
                sp.height = sp._dpTextureHeight;
            }
        }
    }
    return { dpX: sp.dpX, dpy: sp.dpY, dpScaleX: toDPScaleX, dpScaleY: toDPScaleY, dpWidth: sp.dpWidth, dpHeight: sp.dpHeight };
}

1.customShader:
//======================================================================================================
// SYSTEM START 该段注释由系统自动生成，请勿删改
//======================================================================================================
//------------------------------------------------------------------------------------------------------
// 固定参数
//------------------------------------------------------------------------------------------------------
// varying vec2 v_texcoord; //  原始贴图UV坐标
// uniform sampler2D texture; // 原始贴图
// uniform float alpha; // 当前对象实际渲染的透明度
//------------------------------------------------------------------------------------------------------
// 该材质的自定义参数 判定该变量存在的宏：MU材质编号_变量名称
//------------------------------------------------------------------------------------------------------
// uniform float mu1_r; // MU1_r 红
// uniform float mu1_g; // MU1_g 绿
// uniform float mu1_b; // MU1_b 蓝
// uniform float mu1_gray; // MU1_gray 灰度
// uniform float mu1_mr; // MU1_mr 红曝光
// uniform float mu1_mg; // MU1_mg 绿曝光
// uniform float mu1_mb; // MU1_mb 蓝曝光
// uniform float mu1_time; // MU1_time 时间设定
//------------------------------------------------------------------------------------------------------
// shader 代码规则：
// 1.由于多材质合并shader代码运行，函数名必须以mf${材质ID}_开头以避免函数名称冲突，如 mf1_myFunc
// 2.入口函数vec4 mf${材质ID}_main 如 vec4 mf1_main(){return gl_FragColor;}
// 3.原图采样坐标获取：vec2 texCoord = getInRangeTextureCoord(v_texcoord);
// 4.原图采样：vec4 color = getTextureColor(texCoord);
// 5.关于自定义贴图的坐标信息：自定义贴图可能是图集中的一部分，提供了相关信息（mu材质编号_变量名_UVInfo）
//   相对于0~1的坐标系提供了图集的映射信息 vec4 [UV宽度缩放比例, UV高度缩放比例, UV水平偏移量, UV垂直偏移量]
// 6.变量宏（可用于判定该变量是否满足出现条件）：#ifdef MU材质编号_变量名称、#else、#endif
// 7.系统宏 IN_GC_EDITOR=是否在编辑器中运行
// 8.语法：C语言 更多资料请参考GLSL Shader
//======================================================================================================
// SYSTEM END
//======================================================================================================
vec4 mf1_main() {

   float rt1 = 1.0 - (0.6914) * mu1_gray;
   float gt1 = 1.0 - (0.3906) * mu1_gray;
   float bt1 = 1.0 - (0.9180) * mu1_gray;
   float rt0 = 0.3086 * mu1_gray;
   float gt0 = 0.6094 * mu1_gray;
   float bt0 = 0.0820 * mu1_gray;
   mat4 alphaMat = mat4(
        rt1 * mu1_mr, gt0, bt0, mu1_r, 
        rt0, gt1 * mu1_mg, bt0, mu1_g,
        rt0, gt0, bt1*mu1_mb, mu1_b,
        0.0, 0.0, 0.0, 1.0
   );
   vec4 newFragColor = gl_FragColor;
   newFragColor *= alphaMat ;



   newFragColor.r += mu1_r*(mu1_r<0.0?newFragColor.r:1.0);
   newFragColor.g += mu1_g*(mu1_g<0.0?newFragColor.g:1.0);
   newFragColor.b += mu1_b*(mu1_b<0.0?newFragColor.b:1.0);

  // gl_FragColor.r += mu1_unnamed;
   #ifdef MU1_time
     return (newFragColor - gl_FragColor)*mu1_time + gl_FragColor;
   #else
     return newFragColor;
   #endif
}

