2.customPreShaderClacTS:
//------------------------------------------------------------------------------------------------------
// 此处可以编写需要经过客户端代码预先计算再传递的额外Shader uniform（通常情况下是逐帧调用该段代码）
// 可视化制作的自定义变量会自动进行转化为Shader中可用的uniform，此处是用于修改或新增计算后的参数进行传递
// -- sp 可获取使用该材质的显示对象（GameSprite）
// -- shader 可获得或修改即将传递给Shader使用的对应变量的值（类型要保持不变），比如 shader.mu3_xxx
// -- 新增加属性：
//    1.直接在此处编写：shader["mu材质编号_自定义变量名称"] 如 shader["mu3_abc"] = 5;
//    2.在Shader代码中需要自己输入增加的，如 uniform float mu3_abc;
// -- 如果需要缓存计算结果可以自行根据键值对进行缓存，以便减少计算量来提升性能 如 this["xxxx"] = 计算后的结果;
// 【Typescript 与 GLSL 中变量映射】
// -- number             => uniform float
// -- number[]           => uniform vec2/vec3/vec4  根据ts中数值数组的长度决定
// -- Float32Array(16)   => uniform mat4  按顺序对应4x4矩阵数据
//------------------------------------------------------------------------------------------------------
var hueUniformName = "hueColorMat4";
var cacheKey = "__hueValue";
var huaValue = this[cacheKey];
if (!huaValue || huaValue[0] != shader.mu2_hue) {
    var hueColorMat4 = shader[hueUniformName] = new Float32Array(16);
    var hueF = new HueFilter(shader.mu2_hue);
    var colorMat = hueF.colorMat;
    var j = 0;
    for (var i = 0; i < 20; i++) {
        if (i % 5 != 4) {
            hueColorMat4[j++] = colorMat[i];
        }
    }
    this[cacheKey] = [shader.mu2_hue, hueColorMat4];
}
else{
    shader[hueUniformName] = huaValue[1];
}

