   return clamp(t, 0.0, 1.0);
}

float mf14_remap(float t, float a, float b){
    return mf14_sat((t-a)/(b-a));
}

float mf14_linterp(float t) {
	return mf14_sat(1.0 - abs(2.0*t - 1.0));
}

vec3 mf14_spectrum_offset(float t) {
	vec3 ret;
	float lo = step(t,0.5);
	float hi = 1.0 - lo;
	float w = mf14_linterp(mf14_remap(t, 1.0 / 6.0, 5.0 / 6.0));
	float neg_w = 1.0 - w;
	ret = vec3(lo,1.0,hi) * vec3(neg_w, w, neg_w);
	return pow(ret, vec3(1.0 / 2.2, 1.0 / 2.2, 1.0 / 2.2));
}

