	const int NUM_SAMPLES = 10;
	const float RCP_NUM_SAMPLES_F = 1.0 / float(NUM_SAMPLES);

	vec4 sum = vec4(0.0, 0.0, 0.0, 0.0);
    vec3 wsum = vec3(0.0, 0.0, 0.0);

	for(float j = 0.0;j < float(NUM_SAMPLES); ++j){
		float t = j * RCP_NUM_SAMPLES_F;
		new_texcoord.x = mf14_sat(new_texcoord.x + ofs * t);
		vec4 samplecol = texture2D(texture, new_texcoord); 
		vec3 s = mf14_spectrum_offset(t);
		samplecol.rgb = samplecol.rgb * s;
		sum += samplecol;
		wsum += s;
	}

	sum.rgb /= wsum;
	sum.a *= RCP_NUM_SAMPLES_F;
	c.a = sum.a;
	c.rgb = sum.rgb;
	c.rgb = c.rgb;

    return c;
}

