//======================================================================================================
// SYSTEM START 该段注释由系统自动生成，请勿删改
//======================================================================================================
//------------------------------------------------------------------------------------------------------
// 固定参数
//------------------------------------------------------------------------------------------------------
// varying vec2 v_texcoord; //  原始贴图UV坐标
// uniform sampler2D texture; // 原始贴图
// uniform float alpha; // 当前对象实际渲染的透明度
//------------------------------------------------------------------------------------------------------
// 该材质的自定义参数 判定该变量存在的宏：MU材质编号_变量名称
//------------------------------------------------------------------------------------------------------
// uniform float mu18_lineColorR; // MU18_lineColorR 线条颜色r
// uniform float mu18_lineColorG; // MU18_lineColorG 线条颜色g
// uniform float mu18_lineColorB; // MU18_lineColorB 线条颜色b
// uniform float mu18_lineColorA; // MU18_lineColorA 线条颜色a
// uniform float mu18_lineWidth; // MU18_lineWidth 线条宽度
// uniform float mu18_rangeX; // MU18_rangeX X过渡
//------------------------------------------------------------------------------------------------------
// shader 代码规则：
// 1.由于多材质合并shader代码运行，函数名必须以mf${材质ID}_开头以避免函数名称冲突，如 mf18_myFunc
// 2.入口函数vec4 mf${材质ID}_main 如 vec4 mf18_main(){return gl_FragColor;}
// 3.原图采样坐标获取：vec2 texCoord = getInRangeTextureCoord(v_texcoord);
// 4.原图采样：vec4 color = getTextureColor(texCoord);
// 5.关于自定义贴图的坐标信息：自定义贴图可能是图集中的一部分，提供了相关信息（mu材质编号_变量名_UVInfo）
//   相对于0~1的坐标系提供了图集的映射信息 vec4 [UV宽度缩放比例, UV高度缩放比例, UV水平偏移量, UV垂直偏移量]
// 6.变量宏（可用于判定该变量是否满足出现条件）：#ifdef MU材质编号_变量名称、#else、#endif
// 7.系统宏 IN_GC_EDITOR=是否在编辑器中运行
// 8.语法：C语言 更多资料请参考GLSL Shader
//======================================================================================================
// SYSTEM END
//======================================================================================================
vec4 mf18_main(){
   vec4 col = texture2D(texture,v_texcoord);
   vec4 color = vec4(mu18_lineColorR,mu18_lineColorG,mu18_lineColorB,mu18_lineColorA);
   if(v_texcoord.x > mu18_rangeX){
       discard;
   }else if(v_texcoord.x > mu18_rangeX - mu18_lineWidth){
       float offsetX = v_texcoord.x - mu18_rangeX + mu18_lineWidth;
       float xAlpha = offsetX / mu18_lineWidth;
       col = col * (1.0 - xAlpha) + color * xAlpha;
   }
   return col;
}

