                    else if (type == 1) {
                        newCmdParams.dpWidth = res.dpWidth;
                        newCmdParams.dpHeight = res.dpHeight;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}
function refreshImageRealShow(uiBitmap, cmdParams, passageData, needSyncCMDParams, needResizeWhenEmptyImage) {
    if (needSyncCMDParams === void 0) { needSyncCMDParams = false; }
    if (needResizeWhenEmptyImage === void 0) { needResizeWhenEmptyImage = false; }
    var toImage;
    var paramData = cmdParams ? cmdParams : passageData;
    if (paramData.imageUseVar) {
        if (paramData.refImageEnabled) {
            toImage = paramData.refImage ? paramData.refImage : emptyImageURL;
