// 自定义
//------------------------------------------------------------------------------------------------------
function createAnimationMoveData(sp: GameSprite, data: AnimationPassageData) {
    newCmdParams = ObjectUtils.depthClone(data.lastAnimationCmdParams);
    newCmdParams.dpX = sp.dpX;
    newCmdParams.dpY = sp.dpY;
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.dpScaleX = sp.dpScaleX;
    newCmdParams.dpScaleY = sp.dpScaleY;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
/**
 * 当来自操作工具的属性修改时
 * @param type 类别 0-移动 1-缩放 2-旋转  
 * @param realSelObjs 选中的工具
 */
function imageLayerOnToolsChange(type: number, realSelObjs: GameSprite[]) {
    // 根据选中的数据进行获取
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data: AnimationPassageData = sp.data;
        // 如果存在数据且数据类别是当前指令的类别时
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            // 实际坐标转化为深度坐标
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            // 选中的指令是当前指令时：则直接修改属性
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID: number = data.passageID as any;
                if (type == 0) {
                    previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                    previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                }
                else if (type == 1) {
                    previewWin.modifyCommandParam(data.cmd, "dpScaleX", res.dpScaleX);
                    previewWin.modifyCommandParam(data.cmd, "dpScaleY", res.dpScaleY);
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                // 不存在数据时则创建
                if (newCmdParams == null) {
                    createAnimationMoveData(sp, data);
                }
                // 否则直接修改
                else {
                    if (type == 0) {
                        newCmdParams.dpX = sp.dpX;
                        newCmdParams.dpY = sp.dpY;
                    }
                    else if (type == 1) {
                        newCmdParams.dpScaleX = res.dpScaleX;
                        newCmdParams.dpScaleY = res.dpScaleY;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}
/**
 * 刷新图片实际的显示
 * @param uiBitmap 图片对象
 * @param cmdParams 参数（可能为空）
 * @param passageData 通道数据
 * @param needSyncCMDParams [可选] 默认值=false 是否同步指令参数
 * @param needResizeWhenEmptyImage [可选] 默认值=false 是否重置尺寸当空图片时
 */
function refreshAnimationRealShow(displayObject: UIAnimation, cmdParams: ThisCommandParams, passageData: AnimationPassageData, needSyncCMDParams: boolean = false, needResizeWhenEmptyImage: boolean = false) {
    var toAnimation: number;
    var paramData = cmdParams ? cmdParams : passageData;
    // 使用变量的情况下
    if (paramData.objectUseVar) {
        if (paramData.refObjectEnabled) {
            toAnimation = paramData.refObject ? paramData.refObject : 0;
        }
        else {
            toAnimation = 0;
        }
    }
    // 普通图片显示
    else {
        toAnimation = paramData.animation ? paramData.animation : 0;
    }
    displayObject.animationID = toAnimation;
}

