    newCmdData.cmd = newCMD;
    newCmdParams = null;
    previewWin.setData();
}
function imageLayerOnToolsSelectObject(realSelObjs) {
}
function imageLayerPassageClear(passageID, passageData) {
    if (passageData.type == passageType) {
        var a = passageData.displayObject;
        if (a) {
            a.dispose();
        }
    }
}
function imageLayerPassageStart(passageID, passageData) {
    if (!passageData.displayObjectFromShow) {
        return;
    }
    if (passageData.type == passageType) {
        var a = new UIAnimation();
        a.animation.syncLoadWhenAssetExist = true;
        a.data = passageData;
        passageData.displayObject = a;
        a.dpDisplayPriority = passageID;
        refreshAnimationRealShow(passageData.displayObject, null, passageData, false, false);
        imageLayer.addChild(a);
        a.useDPCoordScaleMode = true;
        a.useDPCoord = true;
        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpScaleX = passageData.dpScaleX;
        a.dpScaleY = passageData.dpScaleY;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.playType = 0;
        a.silentMode = true;
        a.showHitEffect = passageData.showHitEffect;
        a.playFps = passageData.playFps;
        a.aniFrame = passageData.aniFrame;
        if (previewWin.currentSelectCmd == passageData.cmd) {
            Callback.CallLaterBeforeRender(function () {
                previewWin.operateObjectTool.selectObjects([a]);
            }, this);
        }
        else if (!previewWin.allowAllDisplayObject) {
            a.mouseEnabled = false;
            a.setTonal(-200, -200, -200, 0);
        }
        previewWin.operateObjectTool.addExcludeOperactionObject(a);
        a.on(EventObject.RIGHT_MOUSE_UP, this, function (a) {
            if (previewWin.viewAreaRightMouseMove)
                return;
            var menuData = [
                {
                    label: "定位到对应的事件",
                    splitLine: true,
                    children: [],
                    execute: "selectEvent",
                    shortcutKey: "F",
                    menuHotKey: "F",
                    enabled: true,
                    visible: true,
                },
                {
                    label: "生成「显示动画事件」",
                    splitLine: false,
                    children: [],
                    execute: "createShowAnimationEvent",
                    shortcutKey: "",
                    menuHotKey: "",
                    enabled: true,
                    visible: true,
                },
                {
                    label: "生成「移动动画事件」",
                    splitLine: false,
                    children: [],
                    execute: "createMoveAnimationEvent",
                    shortcutKey: "",
                    menuHotKey: "",
                    enabled: true,
                    visible: true,
                },
                {
                    label: "生成「消除动画事件」",
                    splitLine: false,
                    children: [],
                    execute: "earseEvent",
                    shortcutKey: "D",
                    menuHotKey: "D",
                    enabled: true,
                    visible: true,
                }
            ];
            var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
            var funcDomain = {
                selectEvent: function () {
                    previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                },
                createShowAnimationEvent: function () {
                    var d = a.data;
                    createAnimationMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showAnimation, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                createMoveAnimationEvent: function () {
                    var d = a.data;
                    createAnimationMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveAnimation, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                earseEvent: function () {
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_earsePicture, {
                        passageIDUseVar: passageData.passageIDUseVar,
                        passageID: passageData.passageID
                    }, isinsertCMDToUp ? -2 : -3, false);
                    previewWin.setData();
                }
            };
            previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
        }, [a]);
    }
}
function createAnimationMoveData(sp, data) {
    newCmdParams = ObjectUtils.depthClone(data.lastAnimationCmdParams);
    newCmdParams.dpX = sp.dpX;
    newCmdParams.dpY = sp.dpY;
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.dpScaleX = sp.dpScaleX;
    newCmdParams.dpScaleY = sp.dpScaleY;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
function imageLayerOnToolsChange(type, realSelObjs) {
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data = sp.data;
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID = data.passageID;
                if (type == 0) {
                    previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                    previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                }
                else if (type == 1) {
                    previewWin.modifyCommandParam(data.cmd, "dpScaleX", res.dpScaleX);
                    previewWin.modifyCommandParam(data.cmd, "dpScaleY", res.dpScaleY);
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                if (newCmdParams == null) {
                    createAnimationMoveData(sp, data);
                }
                else {
                    if (type == 0) {
                        newCmdParams.dpX = sp.dpX;
                        newCmdParams.dpY = sp.dpY;
                    }
                    else if (type == 1) {
                        newCmdParams.dpScaleX = res.dpScaleX;
                        newCmdParams.dpScaleY = res.dpScaleY;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}
function refreshAnimationRealShow(displayObject, cmdParams, passageData, needSyncCMDParams, needResizeWhenEmptyImage) {
    if (needSyncCMDParams === void 0) { needSyncCMDParams = false; }
    if (needResizeWhenEmptyImage === void 0) { needResizeWhenEmptyImage = false; }
    var toAnimation;
    var paramData = cmdParams ? cmdParams : passageData;
    if (paramData.objectUseVar) {
        if (paramData.refObjectEnabled) {
            toAnimation = paramData.refObject ? paramData.refObject : 0;
        }
        else {
            toAnimation = 0;
        }
    }
    else {
        toAnimation = paramData.animation ? paramData.animation : 0;
    }
    displayObject.animationID = toAnimation;
}

3004.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var str = ``;
var passageIDStr = attrs.passageIDUseVar ? `@${attrs.passageIDVar}-${getTypeDataListName(3, attrs.passageIDVar)}` : attrs.passageID;
str += `编号【${passageIDStr}】`;
var objStr = attrs.objectUseVar ? `${attrs.animationVar}-${getTypeDataListName(3, attrs.animationVar)}` : `${attrs.animation}-${getTypeDataListName(15, attrs.animation)}`;
str += `「${objStr}」 `;
if (attrs.posUseVar) {
    var xStr = `@${attrs.dpXVar}`;
    var yStr = `@${attrs.dpYVar}`;
}
else {
    xStr = attrs.dpX as any;
    yStr = attrs.dpY as any;
}
var zStr = attrs.zUseVar ? `@${attrs.dpZVar}` : attrs.dpZ;
if (attrs.sizeUseVar) {
    var wStr = `@${attrs.dpScaleX}`;
    var hStr = `@${attrs.dpScaleY}`;
}
else {
    wStr = (attrs.dpScaleX * 100) + "%";
    hStr = (attrs.dpScaleY * 100) + "%";
}
var roStr = attrs.rotationUseVar ? `@${attrs.rotationVar}` : attrs.rotation;
var alphaStr = attrs.opacityUseVar ? `@${attrs.opacityVar}` : attrs.opacity;
var startFrame = attrs.aniFrameUseVar ? `@${attrs.aniFrameVar}` : attrs.aniFrame;
if (attrs.playType == 0) {
    str += ` 第${startFrame}帧 `;
    str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;

}
else {
    str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;
    str += ` 第${startFrame}帧 ${attrs.playTypeItems[attrs.playType]} `;
}
if (attrs.showHitEffect) {
    str += ` 命中`;
}
if (attrs.silentMode) {
    str += ` 静音`;
}
if (attrs.playType != 0) {
    str += ` 帧率=${attrs.playFps}`;
}
str;

