    // -- 获取储存的通道，没有的话则新建
    if (isPlayMode) {
        // 没有该通道或通道储存类型不匹配或无显示对象的话则忽略掉
        if (!passageData || !passageData.displayObject) return;
        // 清理注册的自动旋转
        if (passageData.autoRotationTimeFunc) {
            os.remove_ENTERFRAME(passageData.autoRotationTimeFunc, this);
        }
        // 如果不存在的话
        if (cmdParams.rotation != 0) {
            var f: Function;
            os.add_ENTERFRAME(f = passageData.autoRotationTimeFunc = (passageData: any, displayObject: GameSprite, cmdParams: ThisCommandParams) => {
                // 该通道的显示对象开始旋转
                if (passageData.displayObject) {
                    displayObject.rotation2 += cmdParams.rotation;
                }
                // 如果已没有显示对象的话就直接清理掉该函数
                else {
                    os.remove_ENTERFRAME(arguments.callee, this);
                }
            }, this, [passageData, passageData.displayObject, cmdParams]);
            f.apply(this, [passageData, passageData.displayObject, cmdParams]);
            // 覆盖函数
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                lastOnCoverPassage.apply(this, arguments);
            }
        }
    }
}
