var passageType = "gcUI";
var PLUGIN_CUSTOM_COMMAND_showUI = 3010;
var PLUGIN_CUSTOM_COMMAND_moveUI = 3011;
var PLUGIN_CUSTOM_COMMAND_closeUI = 3012;
