var passageType = "gcUI";
function runCommand(cmdParams, cmd, isPlayMode) {
    var _this = this;
    var params = cmdParams.changeUIAttr[1];
    var uiID = MathUtils.int(params[2].uiID);
    if (uiID <= 0)
        return;
    var passageID = 1000000 + uiID;
    var passageData = imageLayerPassageData[passageID];
    if (isPlayMode) {
        if (!passageData)
            return;
        var cmdParam = cmdParams.changeUIAttr[1];
        if (cmdParam[5] == 0) {
            var atts = cmdParam[2];
            if (atts) {
                var comps = GameUI.getAllCompChildren(passageData.displayObject, true);
                for (var compID in atts.atts) {
                    var uiComp = comps.keyValue[compID];
                    if (uiComp) {
                        var attsValues = atts.atts[compID][1];
                        for (var attName in attsValues) {
                            var attValue = attsValues[attName];
                            uiComp[attName] = attValue;
                        }
                    }
                }
            }
        }
        else {
            var m = {
                time: cmdParam[0],
                curTime: 1,
                transData: GameUtils.getTransData(cmdParam[1]),
                attrInfos: []
            };
            var atts = cmdParam[2];
            if (atts) {
                var comps = GameUI.getAllCompChildren(passageData.displayObject, true);
                for (var compID in atts.atts) {
                    var uiComp = comps.keyValue[compID];
                    if (uiComp) {
                        var attsValues = atts.atts[compID][1];
                        var useVarAndTransitionAttrs = atts.atts[compID][2];
                        for (var attName in attsValues) {
                            var oldValue = uiComp[attName];
                            var needTween = typeof oldValue == "number";
                            var useVarAndTransition = useVarAndTransitionAttrs[attName];
                            if (useVarAndTransition) {
                                if (!useVarAndTransition.change) {
                                    needTween = false;
                                }
                            }
                            var newValue = attsValues[attName];
                            var attrInfo = { uiComp: uiComp, attName: attName, oldValue: oldValue, needTween: needTween, newValue: newValue };
                            m.attrInfos.push(attrInfo);
                        }
                    }
                }
            }
            if (!passageData.onChangeAttrFrame)
                passageData.onChangeAttrFrame = [];
            var f;
            os.add_ENTERFRAME(f = function (displayObject, cmdParams, cmdParam) {
                var nonTweenType = cmdParam[3];
                var per = m.curTime / m.time;
                for (var i = 0; i < m.attrInfos.length; i++) {
                    var attrInfo = m.attrInfos[i];
                    if (!attrInfo.needTween) {
                        if ((nonTweenType == 0 && m.curTime == 1) || (nonTweenType == 1 && per == 1)) {
                            attrInfo.uiComp[attrInfo.attName] = attrInfo.newValue;
                        }
                    }
                    else {
                        var valuePer = GameUtils.getValueByTransData(m.transData, per);
                        attrInfo.uiComp[attrInfo.attName] = (attrInfo.newValue - attrInfo.oldValue) * valuePer + attrInfo.oldValue;
                    }
                }
                m.curTime++;
                if (per == 1) {
                    os.remove_ENTERFRAME(arguments.callee, _this);
                }
            }, this, [passageData.displayObject, cmdParams, cmdParam]);
            f.apply(this, [passageData.displayObject, cmdParams, cmdParam]);
            passageData.onChangeAttrFrame.push(f);
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                lastOnCoverPassage.apply(this, arguments);
            };
        }
    }
    else {
        if (!passageData) {
            passageData = imageLayerPassageData[passageID] = {};
            passageData.passageID = passageID;
            passageData.type = passageType;
            passageData.cmd = cmd;
            passageData.lastStandAvatarCmdParams = cmdParams;
            passageData.uiID = uiID;
            passageData.dpX = 0;
            passageData.dpY = 0;
            passageData.dpZ = 100;
            passageData.dpScaleX = 1;
            passageData.dpScaleY = 1;
            passageData.rotation = 0;
            passageData.opacity = 1;
            passageData.showType = 0;
        }
        passageData.changeUIAttrMode = true;
        if (passageData.changeUIAttrs == null)
            passageData.changeUIAttrs = [];
        passageData.changeUIAttrs.push(cmdParams.changeUIAttr);
    }
}
