//------------------------------------------------------------------------------------------------------
// 通道数据
//------------------------------------------------------------------------------------------------------
declare class VideoPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    passageIDUseVar: boolean;
    video: string;
    displayObject: UIVideo;
    displayObjectFromShow: boolean; // 区分显示对象来源，是否来自显示对象，否则来自移动对象
    dpX: number;
    dpY: number;
    dpZ: number;
    dpWidth: number;
    dpHeight: number;
    rotation: number;
    opacity: number;
    flip: boolean;
    objectUseVar: boolean;

    playType: number;
    muted: boolean;
    loop: boolean;
    volume: number;
    currentTime: number;

    lastImageCmdParams: ThisCommandParams; // 记录上一个关联的指令的参数
}
//------------------------------------------------------------------------------------------------------
// 自定义变量
//------------------------------------------------------------------------------------------------------
var passageType = "gcVideo";
var PLUGIN_CUSTOM_COMMAND_showVideo: number = 3018;
var PLUGIN_CUSTOM_COMMAND_moveVideo: number = 3019;
var PLUGIN_CUSTOM_COMMAND_earsePicture: number = 3008;
var newCmdParams;
var newCmdData: VideoPassageData;
//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // 使用变量作为通道的话则忽略此指令执行
    if (cmdParams.passageIDUseVar) {
        return;
    }
    // -- 获取通道数据
    var passageData: VideoPassageData = imageLayerPassageData[cmdParams.passageID];
    // -- 播放模式下
    if (isPlayMode) {
        // 覆盖通道，调用上一个占用该通道的指令的覆盖函数（不一定是该指令）
        if (passageData) {
            passageData.onCoverPassage && passageData.onCoverPassage.apply(this, [cmdParams.passageID, passageData]);
        }
    }
    // -- 获取储存的通道，没有的话则新建
    passageData = imageLayerPassageData[cmdParams.passageID] = {} as any;
    // 写入数据：系统必要项
    passageData.passageID = cmdParams.passageID;
    passageData.type = passageType;
    passageData.cmd = cmd;
    passageData.lastImageCmdParams = cmdParams;
    passageData.objectUseVar = cmdParams.objectUseVar;
    // 写入数据：自定义属性（预览不支持变量效果显示）
    // -- 图像显示：变量模式下如果没有参考图的话则显示为 emptyImageURL，否则允许显示为参考图

    passageData.video = cmdParams.video

    passageData.dpX = cmdParams.dpX;
    passageData.dpY = cmdParams.dpY;
    passageData.dpZ = cmdParams.dpZ;
    passageData.dpWidth = cmdParams.dpWidth;
    passageData.dpHeight = cmdParams.dpHeight;
    passageData.rotation = cmdParams.rotation;
    passageData.opacity = cmdParams.opacity;
    passageData.flip = cmdParams.flip;
    passageData.displayObjectFromShow = true;
    passageData.playType = cmdParams.playType;
    passageData.muted = cmdParams.muted;
    passageData.loop = cmdParams.loop;
    passageData.volume = cmdParams.volume;
    passageData.currentTime = cmdParams.currentTime;
    // 播放模式的情况：需要创建对象以便播放
    if (isPlayMode) {
        // 记录覆盖函数，以便其他指令可以覆盖该通道时
        passageData.onCoverPassage = imageLayerPassageClear;
        // 创建图片显示对象
        var a = new UIVideo(false);
        a.useDPCoord = true;
        // 设定显示优先度，以便在同Z轴时，编号越大，显示在越前方
        a.dpDisplayPriority = passageData.passageID;
        a.videoURL = passageData.video;
        a.data = passageData;
        imageLayer.addChild(a);

        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpWidth = passageData.dpWidth;
        a.dpHeight = passageData.dpHeight;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.flip = passageData.flip;

        a.playType = passageData.playType;
        a.muted = passageData.muted;
        a.loop = passageData.loop;
        a.volume = passageData.volume;
        a.currentTime = passageData.currentTime;
        a.playbackRate = cmdParams.playbackRate;


        // tttt
        // a._dpTextureWidth = 1000;
        // a._dpTextureHeight = 1000;
        // a.width = 1000;
        // a.height = 1000;
        // a._uiImage.width = a.width;
        // a._uiImage.height = a.height;
        // a._dpDirty = true;

        // 刷新坐标
        a.dpCoordToRealCoord();
        // 记录显示对象
        passageData.displayObject = a;
    }
}
