}
//------------------------------------------------------------------------------------------------------
// 图像层相关 遍历每个已使用的通道，每个已使用的通道再遍历已使用的指令，调用下面的函数
// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: VideoPassageData) {
    // -- 判断该通道是该类别
    if (passageData.type == passageType) {
        // 卸载创建的显示对象
        var a: UIVideo = passageData.displayObject;
        if (a) {
            a.dispose();
        }
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: VideoPassageData) {
    // 不是来自显示图片的话忽略掉（比如只有移动图片指令实际生效）
    if (!passageData.displayObjectFromShow) {
        return;
    }
    // 通道类别匹配的话
    if (passageData.type == passageType) {
        // -- 创建图片
        var a = new UIVideo();
        a.useDPCoord = true;
        a.data = passageData;
        passageData.displayObject = a;
        // -- 同Z轴时按该优先级出现，即编号越大yue
        a.dpDisplayPriority = passageID;
        // refreshImageRealShow(passageData.displayObject, null, passageData, false, false);
        imageLayer.addChild(a);
        // -- 深度坐标

        a.dpX = passageData.dpX;
        a.dpY = passageData.dpY;
        a.dpZ = passageData.dpZ;
        a.dpWidth = passageData.dpWidth;
        a.dpHeight = passageData.dpHeight;
        a.rotation1 = passageData.rotation;
        a.opacity = passageData.opacity;
        a.flip = passageData.flip;

        // a._dpTextureWidth = 1000;
        // a._dpTextureHeight = 1000;
        // a.width = 1000;
        // a.height = 1000;
        // a._uiImage.width = a.width;
        // a._uiImage.height = a.height;
        // a._dpDirty = true;
        a.dpCoordToRealCoord();


        // -- 如果是当前指令的话则选中它
        if (previewWin.currentSelectCmd == passageData.cmd) {
            Callback.CallLaterBeforeRender(() => {
                previewWin.operateObjectTool.selectObjects([a]);
            }, this)
        }
        else if (!previewWin.allowAllDisplayObject) {
            a.mouseEnabled = false;
            a.setTonal(-200, -200, -200, 0);
        }
        // ---
        a.on(EventObject.RIGHT_MOUSE_UP, this, (a: UIBitmap) => {
            if (previewWin.viewAreaRightMouseMove) return;
            var menuData: any[] = [
                {
                    label: "定位到对应的事件",
                    splitLine: true,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "selectEvent", // 点击后执行的方法名称
                    shortcutKey: "F", //显示的快捷键名称
                    menuHotKey: "F", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「显示视频事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createShowImageEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「移动视频事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createMoveImageEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「消除视频事件」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "earseEvent", // 点击后执行的方法名称
                    shortcutKey: "D", //显示的快捷键名称
                    menuHotKey: "D", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                }
            ];
            // 关于生成的指令是插入在上方还是下方
            var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
            var funcDomain = {
                selectEvent: () => {
                    previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                },
                createShowImageEvent: () => {
                    var d = a.data as VideoPassageData;
                    createImageMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showVideo, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
                },
                createMoveImageEvent: () => {
                    var d = a.data as VideoPassageData;
                    createImageMoveData(a, d);
                    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveVideo, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                    newCmdData.cmd = newCMD;
                    newCmdParams = null;
                    previewWin.setData();
