3018.customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
var str = ``;
var passageIDStr = attrs.passageIDUseVar ? `@${attrs.passageIDVar}-${getTypeDataListName(3, attrs.passageIDVar)}` : attrs.passageID;
str += `编号【${passageIDStr}】`;
var objStr = attrs.objectUseVar ? `${attrs.videoVar}-${getTypeDataListName(3, attrs.videoVar)}` : (attrs.video ? attrs.video.split("/").pop() : "");
str += `「${objStr}」`;
str += `${attrs.playTypeItems[attrs.playType]} `;
str += `音量=${attrs.volume * 100}% `;
if (attrs.playbackRate != 1) {
    str += `速率=${Math.floor(attrs.playbackRate * 100)}% `;
}


if (attrs.posUseVar) {
    var xStr = `@${attrs.dpXVar}`;
    var yStr = `@${attrs.dpYVar}`;
}
else {
    xStr = attrs.dpX as any;
    yStr = attrs.dpY as any;
}
var zStr = attrs.zUseVar ? `@${attrs.dpZVar}` : attrs.dpZ;
if (attrs.sizeUseVar) {
    var wStr = `@${attrs.dpWidth}`;
    var hStr = `@${attrs.dpHeight}`;
}
else {
    wStr = (attrs.dpWidth).toString();
    hStr = (attrs.dpHeight).toString();
}
var roStr = attrs.rotationUseVar ? `@${attrs.rotationVar}` : attrs.rotation;
var alphaStr = attrs.opacityUseVar ? `@${attrs.opacityVar}` : attrs.opacity;
var startFrame = attrs.currentTimeUseVar ? `@${attrs.currentTimeVar}` : attrs.currentTime;
if (attrs.playType == 0) {
    str += ` 从第${startFrame}秒开始播放 `;
    str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;

}
else {
    str += ` 坐标(${xStr},${yStr},${zStr}) 尺寸(${wStr},${hStr}) 旋转度=${roStr} 透明度=${alphaStr}`;
    str += ` 从第${startFrame}秒开始播放 `;
}
if (attrs.loop) {
    str += ` 循环`;
}
if (attrs.muted) {
    str += ` 静音`;
}
str;

3018.customPhraseDescJS:
var str = "";
var passageIDStr = attrs.passageIDUseVar ? "@" + attrs.passageIDVar + "-" + getTypeDataListName(3, attrs.passageIDVar) : attrs.passageID;
str += "\u7F16\u53F7\u3010" + passageIDStr + "\u3011";
var objStr = attrs.objectUseVar ? attrs.videoVar + "-" + getTypeDataListName(3, attrs.videoVar) : (attrs.video ? attrs.video.split("/").pop() : "");
str += "\u300C" + objStr + "\u300D";
str += attrs.playTypeItems[attrs.playType] + " ";
str += "\u97F3\u91CF=" + attrs.volume * 100 + "% ";
if (attrs.playbackRate != 1) {
    str += "\u901F\u7387=" + Math.floor(attrs.playbackRate * 100) + "% ";
}
if (attrs.posUseVar) {
    var xStr = "@" + attrs.dpXVar;
    var yStr = "@" + attrs.dpYVar;
}
else {
    xStr = attrs.dpX;
    yStr = attrs.dpY;
}
var zStr = attrs.zUseVar ? "@" + attrs.dpZVar : attrs.dpZ;
if (attrs.sizeUseVar) {
    var wStr = "@" + attrs.dpWidth;
    var hStr = "@" + attrs.dpHeight;
}
else {
    wStr = (attrs.dpWidth).toString();
    hStr = (attrs.dpHeight).toString();
}
var roStr = attrs.rotationUseVar ? "@" + attrs.rotationVar : attrs.rotation;
var alphaStr = attrs.opacityUseVar ? "@" + attrs.opacityVar : attrs.opacity;
var startFrame = attrs.currentTimeUseVar ? "@" + attrs.currentTimeVar : attrs.currentTime;
if (attrs.playType == 0) {
    str += " \u4ECE\u7B2C" + startFrame + "\u79D2\u5F00\u59CB\u64AD\u653E ";
    str += " \u5750\u6807(" + xStr + "," + yStr + "," + zStr + ") \u5C3A\u5BF8(" + wStr + "," + hStr + ") \u65CB\u8F6C\u5EA6=" + roStr + " \u900F\u660E\u5EA6=" + alphaStr;
}
else {
    str += " \u5750\u6807(" + xStr + "," + yStr + "," + zStr + ") \u5C3A\u5BF8(" + wStr + "," + hStr + ") \u65CB\u8F6C\u5EA6=" + roStr + " \u900F\u660E\u5EA6=" + alphaStr;
    str += " \u4ECE\u7B2C" + startFrame + "\u79D2\u5F00\u59CB\u64AD\u653E ";
}
if (attrs.loop) {
    str += " \u5FAA\u73AF";
}
if (attrs.muted) {
    str += " \u9759\u97F3";
}
str;

3019.cmdPreviewTS:
//------------------------------------------------------------------------------------------------------
// 编辑器预览窗口运行时提供的系统变量
// 关于预览的流程：
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序执行一遍
// -- 3.imageLayerPassageStart 每已使用的通道x每已使用的指令
// 关于播放的流程
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序播放
//------------------------------------------------------------------------------------------------------
/**
 * 当前自定义指令的编号
 */
declare var commandID: number;
/**
 * 图像层通道数据
 */
declare var imageLayerPassageData: any[];
/**
 * 图像层显示对象容器
 */
declare var imageLayer: GameImageLayer;
/**
 * 界面层显示对象容器
 */
declare var uiLayer: GameSprite;
/**
 * 预览窗口
 */
declare var previewWin: {
    /**
     * 清理全部事件指令产生的预览效果
     */
    clearData: Function;
    /**
     * 设置数据，刷新预览效果直到当前选中的指令行
     */
    setData: Function;
    /**
     * 当前选中的指令
     */
    currentSelectCmd: Command;
    /**
     * 视图区域是否右键移动过
     */
    viewAreaRightMouseMove: boolean;
    /**
     * 允许显示全部对象
     */
    allowAllDisplayObject: boolean;
    /**
     * 对象操作工具
     */
    operateObjectTool: {
        // 刷新对象操作工具，比如来自其他地方更改了对象的位置或尺寸等，可使用该函数刷新
        refresh: Function;
        // 选中对象
        selectObjects: (sp: GameSprite[]) => void;
        // 需要排除缩放和旋转工具的对象
        addExcludeOperactionObject: (sp: GameSprite) => void
    }
    /**
     * 选中事件指令
     * @param cmd 事件指令
     * @param sendEvent 派发事件 默认=false 派发事件会刷新属性栏、预览窗口等
     */
    selectCmdMessageByCmd(cmd: Command, sendEvent?: boolean);
    /**
     * 修改指令属性
     * @param cmd 需要修改的指令
     * @param varName 参数变量名称
     * @param newValue 新的值
     */
    modifyCommandParam: (cmd: Command, varName: string, newValue: any) => void;
    /**
     * 新增指令 默认是默认值，如果有写入参数的话则以参数为准 varNameValue
     * @param customID 自定义ID
     * @param varNameValue 额外的变量值，如存在则优先使用该值，否则使用自定义编辑器中的默认值，如 {dpX:100,dpY:200}
     * @param selectedIndex [可选] 默认值=-2  -3=当前选中（之后） -2=当前选中（之前） -1=最尾端 0-N=自定义 
     * @param refreshPreViewArea [可选] 默认值=true 刷新预览区
     */
    insertCustomCMD(customID: number, varNameValue: any, selectedIndex?: number, refreshPreViewArea?: boolean): Command;
    /**
     * 创建菜单
     * var menuData:any[] = [
               {
                   label:"重命名",
                   splitLine:false,// 是否显示分割线
                   children:[], // 存放和本身这个对象是一样的格式
                   execute:"functionName", // 点击后执行的方法名称
                   shortcutKey:"", //显示的快捷键名称
                   menuHotKey:"", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                   enabled:true, // 是否可用，不填写表示true
                   visible:true, // 是否显示，不填写表示true
				   selected:true, // 如果存在则以打钩的形式选择此项
				   group:"myGroupName" // 配合selected，如若存在则同组单选
               }
           ];
     * @param menuData 菜单数据
     * @param x 全局坐标X
     * @param y 全局坐标Y
     * @param executeDomain 函数作用域
     */
    createMenu(menuData: any[], x: number, y: number, executeDomain: any);
    /**
     * 更改显示层
     * @param selectedIndex 0-界面层 1-图像层
     */
    changeLayer(selectedIndex: number): void;
}
//------------------------------------------------------------------------------------------------------
// 通道数据
//------------------------------------------------------------------------------------------------------
declare class VideoPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    passageIDUseVar: boolean;
    timeFunc: Function;
    video: string;
    displayObject: UIVideo;
    displayObjectFromShow: boolean; // 区分显示对象来源，是否来自显示对象，否则来自移动对象
    dpX: number;
    dpY: number;
    dpZ: number;
    dpWidth: number;
    dpHeight: number;
    rotation: number;
    opacity: number;
    flip: boolean;
    objectUseVar: boolean;

    playType: number;
    muted: boolean;
    loop: boolean;
    volume: number;
    currentTime: number;

    lastImageCmdParams: ThisCommandParams; // 记录上一个关联的指令的参数
}
//------------------------------------------------------------------------------------------------------
// 自定义变量
//------------------------------------------------------------------------------------------------------
var passageType = "gcVideo";
var PLUGIN_CUSTOM_COMMAND_showVideo: number = 3018;
var PLUGIN_CUSTOM_COMMAND_moveVideo: number = 3019;
var PLUGIN_CUSTOM_COMMAND_earsePicture: number = 3008;
var newCmdParams;
var newCmdData: VideoPassageData;
//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // -- 使用变量作为通道的话忽略预览效果
    if (cmdParams.passageIDUseVar) {
        return;
    }
    // -- 获取通道数据
    var passageData: VideoPassageData = imageLayerPassageData[cmdParams.passageID];
    // 播放模式的情况
    if (isPlayMode) {
        // 没有该通道或通道储存类型不匹配或无显示对象的话则忽略掉
        if (!passageData || passageData.type != passageType || !passageData.displayObject) return;
        // 清理掉正在执行的移动
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var toDpX = cmdParams.dpX;
        var toDpY = cmdParams.dpY;
        var toDpZ = cmdParams.dpZ;
        var toDpWidth = cmdParams.dpWidth;
        var toDpHeight = cmdParams.dpHeight;
        var toRotation = cmdParams.rotation;
        var toOpacity = cmdParams.opacity;
        // 立即模式：无需清理此行为
        if (cmdParams.timeType == 0) {
            passageData.displayObject.dpX = toDpX;
            passageData.displayObject.dpY = toDpY;
            passageData.displayObject.dpZ = toDpZ;
            passageData.displayObject.dpWidth = toDpWidth;
            passageData.displayObject.dpHeight = toDpHeight;
            passageData.displayObject.rotation1 = toRotation;
            passageData.displayObject.opacity = toOpacity;
            passageData.displayObject.flip = cmdParams.flip;
        }
        // 过渡模式：由于注册了帧刷，需要清理
        else {
            // 下一帧开始移动 1/MAX
            var m = {
                time: cmdParams.time,
                curTime: 1,
                x: passageData.displayObject.dpX,
                y: passageData.displayObject.dpY,
                z: passageData.displayObject.dpZ,
                width: passageData.displayObject.dpWidth,
                height: passageData.displayObject.dpHeight,
                rotation: passageData.displayObject.rotation1,
                opacity: passageData.displayObject.opacity,

                x2: toDpX - passageData.displayObject.dpX,
                y2: toDpY - passageData.displayObject.dpY,
                z2: toDpZ - passageData.displayObject.dpZ,
                width2: toDpWidth - passageData.displayObject.dpWidth,
                height2: toDpHeight - passageData.displayObject.dpHeight,
                rotation2: toRotation - passageData.displayObject.rotation1,
                opacity2: toOpacity - passageData.displayObject.opacity,

                transData: GameUtils.getTransData(cmdParams.trans)
            }
            var f: Function;
            os.add_ENTERFRAME(f = passageData.timeFunc = (uiBitmap: UIBitmap, cmdParams: ThisCommandParams) => {
                // 首帧变化的属性
                if (m.curTime == 1) {
                    uiBitmap.flip = cmdParams.flip;
                }
                var per = m.curTime / m.time;
                var value = GameUtils.getValueByTransData(m.transData, per);
                uiBitmap.dpX = m.x2 * value + m.x;
                uiBitmap.dpY = m.y2 * value + m.y;
                uiBitmap.dpZ = m.z2 * value + m.z;

                uiBitmap.dpWidth = m.width2 * value + m.width;
                uiBitmap.dpHeight = m.height2 * value + m.height;

                uiBitmap.rotation1 = m.rotation2 * value + m.rotation;
                uiBitmap.opacity = m.opacity2 * value + m.opacity;

                m.curTime++;
                if (per == 1) {
                    os.remove_ENTERFRAME(arguments.callee, this);
                }
                uiBitmap.dpCoordToRealCoord();
            }, this, [passageData.displayObject, cmdParams]);
            f.apply(this, [passageData.displayObject, cmdParams]);

            // 覆盖函数
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                if (lastOnCoverPassage) lastOnCoverPassage.apply(this, arguments);
            }
        }
    }
    else {
        // 存在通道但不是该类别的话则忽略掉
        if (passageData && passageData.type != passageType) return;
        if (!passageData) passageData = imageLayerPassageData[cmdParams.passageID] = {} as any;
        // 写入数据
        passageData.passageID = cmdParams.passageID;
        passageData.type = passageType;
        passageData.cmd = cmd;
        if (!passageData.lastImageCmdParams) {
            passageData.lastImageCmdParams = cmdParams;
        }
        else {
            ObjectUtils.clone(cmdParams, passageData.lastImageCmdParams);
        }
        // 属性
        passageData.dpX = cmdParams.dpX;
        passageData.dpY = cmdParams.dpY;
        passageData.dpZ = cmdParams.dpZ;
        passageData.dpWidth = cmdParams.dpWidth;
        passageData.dpHeight = cmdParams.dpHeight;
        passageData.rotation = cmdParams.rotation;
        passageData.opacity = cmdParams.opacity;
        passageData.flip = cmdParams.flip;
    }
}
//------------------------------------------------------------------------------------------------------
// 属性面板修改：当修改该指令的属性时，只会执行该指令对应的「指令源」的这些函数
// 如A指令来自自定义指令32号，每次通过属性面板快速修改A指令的参数b时，则会执行32号指令的下面2个函数
// [current used command] 
//------------------------------------------------------------------------------------------------------
/**
 * 当属性面板中的属性修改前
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 */
function onCommandAttributeBeforeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string) {
    // 修改了通道的话，清理
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.clearData();
    }
}
/**
 * 当属性面板中的属性修改后
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 * @param newValue 修改后的值
 */
function onCommandAttributeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string, newValue: any) {
    // 修改了通道的话，重置所有预览显示
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.setData(false);
        return;
    }
    // -- 使用变量作为通道的话忽略预览效果
    if (cmdParams.passageIDUseVar) {
        return;
    }
    // 获取通道数据
    var passageData: VideoPassageData = imageLayerPassageData[cmdParams.passageID];
    if (!passageData) return;
    // 对象类别匹配且存在显示对象的话
    if (passageData.type == passageType && passageData.displayObject) {
        // 混合模式，需要转换
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else {
            passageData.displayObject[varName] = newValue;
        }
        // -- 刷新操作工具对象（延迟到下一帧）
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
//------------------------------------------------------------------------------------------------------
// 对象操作工具
// [everyone used command type] 遍历每个执行过的自定义指令类别
//------------------------------------------------------------------------------------------------------
/**
 * 当操作工具移动对象时
 * @param realSelObjs 选中的对象
 */
function imageLayerOnToolsChangePostion(realSelObjs: GameSprite[]): void {
    imageLayerOnToolsChange(0, realSelObjs);
}
/**
 * 当操作工具缩放时
 * @param realSelObjs 选中的对象
 */
function imageLayerOnToolsChangeSize(realSelObjs: GameSprite[]) {
    imageLayerOnToolsChange(1, realSelObjs);
}
/**
 * 当操作工具旋转时
 * @param realSelObjs 选中的对象
 */
function imageLayerOnToolsChangeRotation(realSelObjs: GameSprite[]) {
    imageLayerOnToolsChange(2, realSelObjs);
}
/**
 * 当操作工具开始操作时
 */
function imageLayerOnToolsChangeStart(realSelObjs: GameSprite[]) {

}
/**
 * 当操作工具结束操作时
 */
function imageLayerOnToolsChangeEnd(realSelObjs: GameSprite[]) {
    if (newCmdParams == null) return;
    if (realSelObjs.length == 0) return;
    var data: any = realSelObjs[0].data;
    if (!data || data.type != passageType) return;
    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveVideo, newCmdParams, -2, false);
    newCmdData.cmd = newCMD;
    newCmdParams = null;
    previewWin.setData();
}
/**
 * 当操作工具选中对象时
 */
function imageLayerOnToolsSelectObject(realSelObjs: GameSprite[]) {

}
//------------------------------------------------------------------------------------------------------
// 图像层相关 遍历每个已使用的通道，每个已使用的通道再遍历已使用的指令，调用下面的函数
// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: VideoPassageData) {
    // -- 判断该通道是图片的话
    if (passageData.type == passageType) {
        // 清理注册的移动
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var a: UIBitmap = passageData.displayObject;
        // 存在且不是来自显示图片的话
        if (a && !passageData.displayObjectFromShow) {
            a.dispose();
        }
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: VideoPassageData) {
    // -- 当前通道数据是图片类别的话
    if (passageData.type == passageType) {
        // -- 不存在图片显示对象的话，还是要显示一下图片移动
        if (!passageData.displayObject) {
            var a = new UIVideo();
            a.data = passageData;
            imageLayer.addChild(a);
            a.useDPCoord = true;
            a.dpX = passageData.dpX;
            a.dpY = passageData.dpY;
            a.dpZ = passageData.dpZ;
            a.dpWidth = passageData.dpWidth;
            a.dpHeight = passageData.dpHeight;
            a.rotation1 = passageData.rotation;
            a.opacity = passageData.opacity;
            a.flip = passageData.flip;
            passageData.displayObject = a;
            // -- 如果是当前指令的话则选中它
            if (previewWin.currentSelectCmd == passageData.cmd) {
                Callback.CallLaterBeforeRender(() => {
                    previewWin.operateObjectTool.selectObjects([a]);
                }, this)
            }
            else if (!previewWin.allowAllDisplayObject) {
                a.mouseEnabled = false;
                a.setTonal(-200, -200, -200, 0);
            }
            // ---
            a.on(EventObject.RIGHT_MOUSE_UP, this, () => {
                if (previewWin.viewAreaRightMouseMove) return;
                var menuData: any[] = [
                    {
                        label: "定位到对应的事件",
                        splitLine: true,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "selectEvent", // 点击后执行的方法名称
                        shortcutKey: "F", //显示的快捷键名称
                        menuHotKey: "F", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    },
                    {
                        label: "生成「显示视频事件」",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "createShowImageEvent", // 点击后执行的方法名称
                        shortcutKey: "", //显示的快捷键名称
                        menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    },
                    {
                        label: "生成「移动视频事件」",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "createMoveImageEvent", // 点击后执行的方法名称
                        shortcutKey: "", //显示的快捷键名称
                        menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    },
                    {
                        label: "生成「消除视频事件」",
                        splitLine: false,// 是否显示分割线
                        children: [], // 存放和本身这个对象是一样的格式
                        execute: "earseEvent", // 点击后执行的方法名称
                        shortcutKey: "D", //显示的快捷键名称
                        menuHotKey: "D", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                        enabled: true, // 是否可用，不填写表示true
                        visible: true, // 是否显示，不填写表示true
                    }
                ];
                // 关于生成的指令是插入在上方还是下方
                var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
                var funcDomain = {
                    selectEvent: () => {
                        previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                    },
                    createShowImageEvent: () => {
                        var d = a.data as VideoPassageData;
                        createImageMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showVideo, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    createMoveImageEvent: () => {
                        var d = a.data as VideoPassageData;
                        createImageMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveVideo, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    earseEvent: () => {
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_earsePicture, {
                            passageIDUseVar: passageData.passageIDUseVar,
                            passageID: passageData.passageID
                        }, isinsertCMDToUp ? -2 : -3, false);
                        previewWin.setData();
                    }
                };
                previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
            });
        }
    }
}
//------------------------------------------------------------------------------------------------------
// 自定义
//------------------------------------------------------------------------------------------------------
function createImageMoveData(sp: GameSprite, data: VideoPassageData) {
    newCmdParams = ObjectUtils.depthClone(data.lastImageCmdParams);
    newCmdParams.dpX = sp.dpX;
    newCmdParams.dpY = sp.dpY;
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.dpWidth = sp.dpWidth;
    newCmdParams.dpHeight = sp.dpHeight;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
/**
 * 当来自操作工具的属性修改时
 * @param type 类别 0-移动 1-缩放 2-旋转  
 * @param realSelObjs 选中的工具
 */
function imageLayerOnToolsChange(type: number, realSelObjs: GameSprite[]) {
    // 根据选中的数据进行获取
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data: VideoPassageData = sp.data;
        // 如果存在数据且数据类别是当前指令的类别时
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            // 实际坐标转化为深度坐标
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            // 选中的指令是当前指令时：则直接修改属性
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID: number = data.passageID as any;
                if (type == 0) {
                    previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                    previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                }
                else if (type == 1) {
                    previewWin.modifyCommandParam(data.cmd, "dpWidth", res.dpWidth);
                    previewWin.modifyCommandParam(data.cmd, "dpHeight", res.dpHeight);
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                // 不存在数据时则创建
                if (newCmdParams == null) {
                    createImageMoveData(sp, data);
                }
                // 否则直接修改
                else {
                    if (type == 0) {
                        newCmdParams.dpX = sp.dpX;
                        newCmdParams.dpY = sp.dpY;
                    }
                    else if (type == 1) {
                        newCmdParams.dpWidth = res.dpWidth;
                        newCmdParams.dpHeight = res.dpHeight;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
    }
}

3019.cmdPreviewJS:
var passageType = "gcVideo";
var PLUGIN_CUSTOM_COMMAND_showVideo = 3018;
var PLUGIN_CUSTOM_COMMAND_moveVideo = 3019;
var PLUGIN_CUSTOM_COMMAND_earsePicture = 3008;
var newCmdParams;
var newCmdData;
function runCommand(cmdParams, cmd, isPlayMode) {
    var _this = this;
    if (cmdParams.passageIDUseVar) {
        return;
    }
    var passageData = imageLayerPassageData[cmdParams.passageID];
    if (isPlayMode) {
        if (!passageData || passageData.type != passageType || !passageData.displayObject)
            return;
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var toDpX = cmdParams.dpX;
        var toDpY = cmdParams.dpY;
        var toDpZ = cmdParams.dpZ;
        var toDpWidth = cmdParams.dpWidth;
        var toDpHeight = cmdParams.dpHeight;
        var toRotation = cmdParams.rotation;
        var toOpacity = cmdParams.opacity;
        if (cmdParams.timeType == 0) {
            passageData.displayObject.dpX = toDpX;
            passageData.displayObject.dpY = toDpY;
            passageData.displayObject.dpZ = toDpZ;
            passageData.displayObject.dpWidth = toDpWidth;
            passageData.displayObject.dpHeight = toDpHeight;
            passageData.displayObject.rotation1 = toRotation;
            passageData.displayObject.opacity = toOpacity;
            passageData.displayObject.flip = cmdParams.flip;
        }
        else {
            var m = {
                time: cmdParams.time,
                curTime: 1,
                x: passageData.displayObject.dpX,
                y: passageData.displayObject.dpY,
                z: passageData.displayObject.dpZ,
                width: passageData.displayObject.dpWidth,
                height: passageData.displayObject.dpHeight,
                rotation: passageData.displayObject.rotation1,
                opacity: passageData.displayObject.opacity,
                x2: toDpX - passageData.displayObject.dpX,
                y2: toDpY - passageData.displayObject.dpY,
                z2: toDpZ - passageData.displayObject.dpZ,
                width2: toDpWidth - passageData.displayObject.dpWidth,
                height2: toDpHeight - passageData.displayObject.dpHeight,
                rotation2: toRotation - passageData.displayObject.rotation1,
                opacity2: toOpacity - passageData.displayObject.opacity,
                transData: GameUtils.getTransData(cmdParams.trans)
            };
            var f;
            os.add_ENTERFRAME(f = passageData.timeFunc = function (uiBitmap, cmdParams) {
                if (m.curTime == 1) {
                    uiBitmap.flip = cmdParams.flip;
                }
                var per = m.curTime / m.time;
                var value = GameUtils.getValueByTransData(m.transData, per);
                uiBitmap.dpX = m.x2 * value + m.x;
                uiBitmap.dpY = m.y2 * value + m.y;
                uiBitmap.dpZ = m.z2 * value + m.z;
                uiBitmap.dpWidth = m.width2 * value + m.width;
                uiBitmap.dpHeight = m.height2 * value + m.height;
                uiBitmap.rotation1 = m.rotation2 * value + m.rotation;
                uiBitmap.opacity = m.opacity2 * value + m.opacity;
                m.curTime++;
                if (per == 1) {
                    os.remove_ENTERFRAME(arguments.callee, _this);
                }
                uiBitmap.dpCoordToRealCoord();
            }, this, [passageData.displayObject, cmdParams]);
            f.apply(this, [passageData.displayObject, cmdParams]);
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                os.remove_ENTERFRAME(f, this);
                if (lastOnCoverPassage)
                    lastOnCoverPassage.apply(this, arguments);
            };
        }
    }
    else {
        if (passageData && passageData.type != passageType)
            return;
        if (!passageData)
            passageData = imageLayerPassageData[cmdParams.passageID] = {};
        passageData.passageID = cmdParams.passageID;
        passageData.type = passageType;
        passageData.cmd = cmd;
        if (!passageData.lastImageCmdParams) {
            passageData.lastImageCmdParams = cmdParams;
        }
        else {
            ObjectUtils.clone(cmdParams, passageData.lastImageCmdParams);
        }
        passageData.dpX = cmdParams.dpX;
        passageData.dpY = cmdParams.dpY;
        passageData.dpZ = cmdParams.dpZ;
        passageData.dpWidth = cmdParams.dpWidth;
        passageData.dpHeight = cmdParams.dpHeight;
        passageData.rotation = cmdParams.rotation;
        passageData.opacity = cmdParams.opacity;
        passageData.flip = cmdParams.flip;
    }
}
function onCommandAttributeBeforeChange(cmd, cmdParams, varName) {
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.clearData();
    }
}
function onCommandAttributeChange(cmd, cmdParams, varName, newValue) {
    if (varName == "passageID" || varName == "passageIDUseVar") {
        previewWin.setData(false);
        return;
    }
    if (cmdParams.passageIDUseVar) {
        return;
    }
    var passageData = imageLayerPassageData[cmdParams.passageID];
    if (!passageData)
        return;
    if (passageData.type == passageType && passageData.displayObject) {
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else {
            passageData.displayObject[varName] = newValue;
        }
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
function imageLayerOnToolsChangePostion(realSelObjs) {
    imageLayerOnToolsChange(0, realSelObjs);
}
function imageLayerOnToolsChangeSize(realSelObjs) {
    imageLayerOnToolsChange(1, realSelObjs);
}
function imageLayerOnToolsChangeRotation(realSelObjs) {
    imageLayerOnToolsChange(2, realSelObjs);
}
function imageLayerOnToolsChangeStart(realSelObjs) {
}
function imageLayerOnToolsChangeEnd(realSelObjs) {
    if (newCmdParams == null)
        return;
    if (realSelObjs.length == 0)
        return;
    var data = realSelObjs[0].data;
    if (!data || data.type != passageType)
        return;
    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveVideo, newCmdParams, -2, false);
    newCmdData.cmd = newCMD;
    newCmdParams = null;
    previewWin.setData();
}
function imageLayerOnToolsSelectObject(realSelObjs) {
}
function imageLayerPassageClear(passageID, passageData) {
    if (passageData.type == passageType) {
        if (passageData.timeFunc) {
            os.remove_ENTERFRAME(passageData.timeFunc, this);
        }
        var a = passageData.displayObject;
        if (a && !passageData.displayObjectFromShow) {
            a.dispose();
        }
    }
}
function imageLayerPassageStart(passageID, passageData) {
    if (passageData.type == passageType) {
        if (!passageData.displayObject) {
            var a = new UIVideo();
            a.data = passageData;
            imageLayer.addChild(a);
            a.useDPCoord = true;
            a.dpX = passageData.dpX;
            a.dpY = passageData.dpY;
            a.dpZ = passageData.dpZ;
            a.dpWidth = passageData.dpWidth;
            a.dpHeight = passageData.dpHeight;
            a.rotation1 = passageData.rotation;
            a.opacity = passageData.opacity;
            a.flip = passageData.flip;
            passageData.displayObject = a;
            if (previewWin.currentSelectCmd == passageData.cmd) {
                Callback.CallLaterBeforeRender(function () {
                    previewWin.operateObjectTool.selectObjects([a]);
                }, this);
            }
            else if (!previewWin.allowAllDisplayObject) {
                a.mouseEnabled = false;
                a.setTonal(-200, -200, -200, 0);
            }
            a.on(EventObject.RIGHT_MOUSE_UP, this, function () {
                if (previewWin.viewAreaRightMouseMove)
                    return;
                var menuData = [
                    {
                        label: "定位到对应的事件",
                        splitLine: true,
                        children: [],
                        execute: "selectEvent",
                        shortcutKey: "F",
                        menuHotKey: "F",
                        enabled: true,
                        visible: true,
                    },
                    {
                        label: "生成「显示视频事件」",
                        splitLine: false,
                        children: [],
                        execute: "createShowImageEvent",
                        shortcutKey: "",
                        menuHotKey: "",
                        enabled: true,
                        visible: true,
                    },
                    {
                        label: "生成「移动视频事件」",
                        splitLine: false,
                        children: [],
                        execute: "createMoveImageEvent",
                        shortcutKey: "",
                        menuHotKey: "",
                        enabled: true,
                        visible: true,
                    },
                    {
                        label: "生成「消除视频事件」",
                        splitLine: false,
                        children: [],
                        execute: "earseEvent",
                        shortcutKey: "D",
                        menuHotKey: "D",
                        enabled: true,
                        visible: true,
                    }
                ];
                var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
                var funcDomain = {
                    selectEvent: function () {
                        previewWin.selectCmdMessageByCmd(passageData.cmd, true);
                    },
                    createShowImageEvent: function () {
                        var d = a.data;
                        createImageMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showVideo, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    createMoveImageEvent: function () {
                        var d = a.data;
                        createImageMoveData(a, d);
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveVideo, newCmdParams, isinsertCMDToUp ? -2 : -3, false);
                        newCmdData.cmd = newCMD;
                        newCmdParams = null;
                        previewWin.setData();
                    },
                    earseEvent: function () {
                        var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_earsePicture, {
                            passageIDUseVar: passageData.passageIDUseVar,
                            passageID: passageData.passageID
                        }, isinsertCMDToUp ? -2 : -3, false);
                        previewWin.setData();
                    }
                };
                previewWin.createMenu(menuData, stage.mouseX, stage.mouseY, funcDomain);
            });
        }
    }
}
function createImageMoveData(sp, data) {
    newCmdParams = ObjectUtils.depthClone(data.lastImageCmdParams);
    newCmdParams.dpX = sp.dpX;
    newCmdParams.dpY = sp.dpY;
    newCmdParams.dpZ = sp.dpZ;
    newCmdParams.rotation = sp.rotation1;
    newCmdParams.dpWidth = sp.dpWidth;
    newCmdParams.dpHeight = sp.dpHeight;
    newCmdParams.passageID = data.passageID;
    newCmdData = data;
}
function imageLayerOnToolsChange(type, realSelObjs) {
    for (var i = 0; i < realSelObjs.length; i++) {
        var sp = realSelObjs[i];
        var data = sp.data;
        if (data && data.type == passageType && data.cmd.customID == commandID) {
            var res = sp.realCoordToDPCoord(type == 0, type == 1);
            if (previewWin.currentSelectCmd == data.cmd) {
                var passageID = data.passageID;
                if (type == 0) {
                    previewWin.modifyCommandParam(data.cmd, "dpX", sp.dpX);
                    previewWin.modifyCommandParam(data.cmd, "dpY", sp.dpY);
                }
                else if (type == 1) {
                    previewWin.modifyCommandParam(data.cmd, "dpWidth", res.dpWidth);
                    previewWin.modifyCommandParam(data.cmd, "dpHeight", res.dpHeight);
                }
                else {
                    previewWin.modifyCommandParam(data.cmd, "rotation", sp.rotation);
                }
            }
            else {
                if (newCmdParams == null) {
                    createImageMoveData(sp, data);
                }
                else {
                    if (type == 0) {
                        newCmdParams.dpX = sp.dpX;
                        newCmdParams.dpY = sp.dpY;
                    }
                    else if (type == 1) {
                        newCmdParams.dpWidth = res.dpWidth;
                        newCmdParams.dpHeight = res.dpHeight;
                    }
                    else {
                        newCmdParams.rotation = sp.rotation;
                    }
                }
            }
        }
