/**
 * kds的图像震动插件用的通道数据
 */
declare class kdsImageShakePassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了2号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;
}
// 图像层震动的场合 避免与其他冲突，如果有冲突，需要修改此处
var imageLayerModePassageID = 2005631;
// 画面震动的场合 避免与其他冲突，如果有冲突，需要修改此处
var uiLayerModePassageID = 2005632;
// 画面震动的场合 避免与其他冲突，如果有冲突，需要修改此处
var stageLayerModePassageID = 2005633;
//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // 使用变量作为通道的话则忽略此指令执行
    if (cmdParams.passageIDUseVar) {
        return;
    }
    // -- 获取通道数据
    var passageData: any;
    // -- 指定的图像通道
    if (cmdParams.type == 0) {
        passageData = imageLayerPassageData[cmdParams.passageID];
        // 没有该通道或通道储存类型不匹配或无显示对象的话则忽略掉
        if (!passageData || !passageData.displayObject) return;
    }
    // -- 图像层
    else if (cmdParams.type == 1) {
        passageData = imageLayerPassageData[imageLayerModePassageID];
        // 没有该通道则新建
        if (!passageData) {
            passageData = imageLayerPassageData[imageLayerModePassageID] = {
                passageID: imageLayerModePassageID,
                displayObject: imageLayer
            } as any;
        }
    }
    // -- 界面
    else if (cmdParams.type == 2) {
        passageData = imageLayerPassageData[uiLayerModePassageID];
        // 没有该通道则新建
        if (!passageData) {
            passageData = imageLayerPassageData[uiLayerModePassageID] = {
                passageID: uiLayerModePassageID,
                displayObject: uiLayer
            } as any;
        }
    }
    // -- 画面
    else if (cmdParams.type == 3) {
        passageData = imageLayerPassageData[stageLayerModePassageID];
        // 没有该通道则新建
        if (!passageData) {
            passageData = imageLayerPassageData[stageLayerModePassageID] = {
                passageID: stageLayerModePassageID,
                displayObject: imageLayer
            } as any;
        }
    }
    // -- 获取储存的通道，没有的话则新建
    if (isPlayMode) {
        // 清理震动
        if (passageData.kds1_imageShakeClear) {
            passageData.kds1_imageShakeClear.run();
            passageData.kds1_imageShakeClear = null;
        }
        if (passageData.kds1_imageShake) {
            os.remove_ENTERFRAME(passageData.kds1_imageShake, passageData);
            passageData.kds1_imageShake = null;
        }
        if (cmdParams.shakeTimeType == 2) {
            return;
        }
        // 震动函数
        var f: Function;
        var dx = 0;
        var dy = 0;
        var duringFrame = cmdParams.shakeTimeType == 0 ? cmdParams.duringFrame : null;
        var clearDisplayObjectShake = function (displayObject: GameSprite, type: number, isOver: boolean = true) {
            if (type == 1) {
                // 如果与震动位置相同的话才还原，否则不还原
                if (imageLayer["__imageShake_kds1_curX"] == imageLayer.camera.offsetX && imageLayer["__imageShake_kds1_curY"] == imageLayer.camera.offsetY) {
                    imageLayer.camera.offsetX -= dx;
                    imageLayer.camera.offsetY -= dy;
                }
            }
            else if (type == 2) {
                // 如果与震动位置相同的话才还原，否则不还原
                if (uiLayer["__imageShake_kds1_curX"] == uiLayer.x && uiLayer["__imageShake_kds1_curY"] == uiLayer.y) {
                    uiLayer.x -= dx;
                    uiLayer.y -= dy;
                    if (GameDialog["container"]) {
                        GameDialog["container"].x -= dx;
                        GameDialog["container"].y -= dy;
                    }
                }
            }
            else if (type == 3) {
                // 如果与震动位置相同的话才还原，否则不还原
                if ((imageLayer.parent as GameSprite)["__imageShake_kds1_curX"] == (imageLayer.parent as GameSprite).x && (imageLayer.parent as GameSprite)["__imageShake_kds1_curY"] == (imageLayer.parent as GameSprite).y) {
                    (imageLayer.parent as GameSprite).x -= dx;
                    (imageLayer.parent as GameSprite).y -= dy;
                }
            }
            else if (displayObject) {
                // 如果与震动位置相同的话才还原，否则不还原
                if (displayObject["__imageShake_kds1_curX"] == displayObject.dpX && displayObject["__imageShake_kds1_curY"] == displayObject.dpY) {
                    displayObject.dpX -= dx;
                    displayObject.dpY -= dy;
                }
            }
            if (isOver) {
                trace("结束并还原");
            }
            else {
                trace("还原")
            }
            dx = dy = 0;
        }
        f = (passageData: any, displayObject: GameSprite, cmdParams: ThisCommandParams) => {
            // 该通道的显示对象开始旋转
            var newDx = Math.floor(cmdParams.dx * 2 * Math.random() - cmdParams.dx);
            var newDy = Math.floor(cmdParams.dy * 2 * Math.random() - cmdParams.dy);
            if (displayObject) {
                clearDisplayObjectShake(displayObject, cmdParams.type, false);
                dx = newDx;
                dy = newDy;
                if (cmdParams.type == 1) {
                    imageLayer.camera.offsetX += dx;
                    imageLayer.camera.offsetY += dy;
                    imageLayer.updateFrame(true);
                    imageLayer["__imageShake_kds1_curX"] = imageLayer.camera.offsetX;
                    imageLayer["__imageShake_kds1_curY"] = imageLayer.camera.offsetY;
                }
                else if (cmdParams.type == 2) {
                    uiLayer.x += dx;
                    uiLayer.y += dy;
                    if (GameDialog["container"]) {
                        GameDialog["container"].x += dx;
                        GameDialog["container"].y += dy;
                    }
                    uiLayer["__imageShake_kds1_curX"] = uiLayer.x;
                    uiLayer["__imageShake_kds1_curY"] = uiLayer.y;
                }
                else if (cmdParams.type == 3) {
                    (imageLayer.parent as GameSprite).x += dx;
                    (imageLayer.parent as GameSprite).y += dy;
                    (imageLayer.parent as GameSprite)["__imageShake_kds1_curX"] = (imageLayer.parent as GameSprite).x;
                    (imageLayer.parent as GameSprite)["__imageShake_kds1_curY"] = (imageLayer.parent as GameSprite).y;
                }
                else {
                    displayObject.dpX += dx;
                    displayObject.dpY += dy;
                    displayObject.dpCoordToRealCoord();
                    displayObject["__imageShake_kds1_curX"] = displayObject.dpX;
                    displayObject["__imageShake_kds1_curY"] = displayObject.dpY;
                }
                // 
                if (duringFrame != null) {
                    duringFrame--;
                    if (duringFrame == 0) {
                        if (passageData.kds1_imageShakeClear) {
                            passageData.kds1_imageShakeClear.run();
                            passageData.kds1_imageShakeClear = null;
                        }
                        os.remove_ENTERFRAME(arguments.callee, passageData);
                    }
                }
            }
            // 如果已没有显示对象的话就直接清理掉该函数
            else {
                if (passageData.kds1_imageShakeClear) {
                    passageData.kds1_imageShakeClear.run();
                    passageData.kds1_imageShakeClear = null;
                }
                os.remove_ENTERFRAME(arguments.callee, passageData);
            }
        };
        // 记录新的震动函数
        passageData.kds1_imageShakeClear = Callback.New(clearDisplayObjectShake, this, [passageData.displayObject, cmdParams.type]);
        passageData.kds1_imageShake = f;
        // 指定的图像对象
        if (cmdParams.type == 0) {
            // 覆盖函数
            var lastOnCoverPassage = passageData.onCoverPassage;
            passageData.onCoverPassage = function () {
                if (passageData.kds1_imageShakeClear) {
                    passageData.kds1_imageShakeClear.run();
                    passageData.kds1_imageShakeClear = null;
                }
                os.remove_ENTERFRAME(f, passageData);
                lastOnCoverPassage.apply(this, arguments);
            }
        }
        os.add_ENTERFRAME(f, passageData, [passageData, passageData.displayObject, cmdParams]);
        f.apply(passageData, [passageData, passageData.displayObject, cmdParams]);
    }
}
