// 当前的事件触发器：trigger: CommandTrigger;
const ur = new HttpRequest();
const input: string = Game.player.variable.getString(2);

// vjudge API
ur.send('https://test.rct.ai/z', JSON.stringify({
    number: 1,
    length: 90,
    top_p: 0.4,
    temperature: 0.8,
    prompt: input,
}), 'post', 'json', ["Content-Type", "application/json"]);

ur.on(EventObject.COMPLETE, this, (content) => {
    Game.player.variable.setString(3, content.result[0].replace(/^[\s,]+/, '').replace(/(.{30})/g, "$1\n"));
    Game.player.variable.setVariable(2, 1);
});

ur.on(EventObject.ERROR, this, (error: string) => {
    // request is error flag
    Game.player.variable.setVariable(2, 2);
});
